
public class Declaraciones_Operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=5; //Primera linea que se lee
		int b; // Segunda que se lee
		
		b=7; // Declaración de (int b)
		
		int c=b+a; // Suma de a+b
		
		c++; // incremento del valor en +1
		
		
		System.out.println(c);
		
		c+=18; // Para sacar dos valores de incremento diferentes por consola haremos otro System.out.println(c)
		
		System.out.println(c);
		
		c-=3; // Ahora usamos otro operador, esta vez de resta
		
		System.out.println(c);
		
		
		

	}

}
